package com.sascom.chickenstock.account.domain; import jakarta.persistence.*; import lombok.*; @Entity @Table(name="account") @Getter @Setter @NoArgsConstructor public class Account { @Id @GeneratedValue(strategy=GenerationType.IDENTITY) private Long id; private Long memberId; private Long competitionId; @Column(nullable=false) private Long balance=50_000_000L; public void add(long a){balance+=a;} public void deduct(long a){ if(balance<a) throw new IllegalStateException("잔액 부족"); balance-=a; } }