package com.sascom.chickenstock.portfolio.api; import com.sascom.chickenstock.portfolio.api.dto.*; import com.sascom.chickenstock.portfolio.domain.service.PortfolioService; import lombok.RequiredArgsConstructor; import org.springframework.web.bind.annotation.*; @RestController @RequestMapping("/v1/positions") @RequiredArgsConstructor public class PortfolioController { private final PortfolioService service; @PostMapping("/{accountId}/apply-fill") public PortfolioApplyFillResponse applyFill(@PathVariable Long accountId, @RequestBody PortfolioApplyFillRequest req){ return service.applyFill(req); } }